import React, {useState, useEffect, useRef} from 'react';
import {Container} from 'react-bootstrap';
import {data} from './components/data';
import Header from './components/header';
import Card from './components/card';
import Btn from './components/btn';

function App() {
  const [state, setState] = useState(data);
  let texti= useRef();
  console.log(texti)
    useEffect(()=>{
      setState([])
    },[])
    const change= ()=>{
      if(texti.current.innerText !== 'undefined'){
        texti.current.innerText = 'mmmmm';
      }
      console.log(texti.current.innerText)
    }
    change()
    console.log('#############################')
    console.log(state)
    console.log('#############################')
    const clearAll= ()=>{
      setState([])
      console.log(state)
    }
    const showAll= ()=>{
      setState(data)
      console.log(state)
    }
  return (
    <div className="font mx-auto">
      <Container className="mx-auto py-5">
        <Header data={data.length}/>
        <Card Key={Math.random()} data={state} texti={texti}/>
        <Btn clearAll={clearAll}  showAll={showAll}/>
      </Container>
    </div>
  )
}

export default App;
