import React from 'react';
import {Button,Container,Row,Col} from 'react-bootstrap';

function App() {
  const data=[{id:1,name:'طلال',time:'الخامسة مساء',img:'1.jpg'},
              {id:2,name:'أنور',time:'الثانية ظهرا',img:'1.jpg'},
              {id:3,name:'فهد',time:'السابعة صباحا',img:'1.jpg'},
              {id:4,name:'ضاري',time:'الرابعة عصرا',img:'1.jpg'}]
  return (
    <div className="font mx-auto">
      <Container className="mx-auto py-5">
        <Row className="justify-content-center mb-3">
          <Col sm={8} className="">لديك اليوم {data.length} مواعيد</Col>
        </Row>
        <Row className="justify-content-center mb-3 h">
          <Col sm={8} className="">
            <div className="cardi">
              {
                data.map(item=>{
                  return(
                    <div className="d-flex gap-3 border-bottom my-1">
                      <img alt="person" className='photo' src={item.img}/>
                      <div>
                        <p className="d-inline fs-5">{item.name}</p>
                        <p className="fs-6 text-muted">{item.time}</p>
                      </div>
                  </div>
                  )
                })
              }
            </div> 
          </Col>
        </Row>
        <Row className="justify-content-center mb-3">
          <Col sm={8} className="">
            <div className="d-flex justify-content-between">
              <Button className="btn btn-primary button">عرض المواعيد</Button>
              <Button className="btn btn-primary button">مسح الكل</Button>
            </div>

          </Col>
        </Row>
      </Container>
    </div>
  )
}

export default App;
